mapper.select:
SELECT r.*, u.username, m.title as movie_title FROM movie_rating r LEFT JOIN user u ON r.user_id = u.id LEFT JOIN movie m ON r.movie_id = m.id
AND r.movie_id = #{movieId}
AND r.user_id = #{userId}
ORDER BY r.create_time DESC

SELECT COALESCE(AVG(score), 0)
FROM movie_rating
WHERE movie_id = #{movieId}
