mapper.select:
SELECT 
    s.*,
    m.title as movieTitle,
    c.name as cinemaName,
    h.name as hallName
FROM movie_schedule s
LEFT JOIN movie m ON s.movie_id = m.id
LEFT JOIN cinema c ON s.cinema_id = c.id
LEFT JOIN cinema_hall h ON s.hall_id = h.id

    AND m.status = 1

        AND s.movie_id = #{movieId}


        AND s.cinema_id = #{cinemaId}


        AND s.hall_id = #{hallId}


        AND s.start_time >= #{startTime}


        AND s.end_time <= #{endTime}


        AND s.status = #{status}


ORDER BY s.start_time ASC

SELECT *
FROM movie_schedule
WHERE hall_id = #{hallId}
AND status = 1
AND (
    (start_time BETWEEN #{startTime} AND #{endTime})
    OR (end_time BETWEEN #{startTime} AND #{endTime})
    OR (start_time <= #{startTime} AND end_time >= #{endTime})
)

    AND id != #{excludeId}

SELECT 
    s.*,
    m.title as movieTitle,
    c.name as cinemaName,
    h.name as hallName
FROM movie_schedule s
LEFT JOIN movie m ON s.movie_id = m.id
LEFT JOIN cinema c ON s.cinema_id = c.id
LEFT JOIN cinema_hall h ON s.hall_id = h.id

    AND m.status = 1
    AND s.start_time >= #{currentTime}

        AND s.movie_id = #{movieId}


        AND s.cinema_id = #{cinemaId}


        AND s.hall_id = #{hallId}


        AND s.status = #{status}


ORDER BY s.start_time ASC

